mapper.select:
SELECT attNum, empNum, attDate, checkIn, checkOut, workHours, attState
FROM attendance
WHERE attNum = #{attNum}

SELECT attNum, empNum, attDate, checkIn, checkOut, workHours, attState
FROM attendance
WHERE empNum = #{empNum}
  AND attDate BETWEEN #{from} AND #{to}
ORDER BY attDate DESC, attNum DESC
