mapper.select:
SELECT voucherId, memNum, memberName, startDate, endDate
FROM voucher_log
WHERE memNum = #{memNum}

mapper.update:
UPDATE voucher_log
SET endDate = #{endDate}
WHERE memNum = #{memNum}

UPDATE voucher_log
SET startDate = SYSDATE,
    endDate = #{endDate}
WHERE memNum = #{memNum}

UPDATE voucher_log
SET endDate = #{endDate}
WHERE memNum = #{memNum}

UPDATE voucher_log
SET endDate = #{endDate}
WHERE memNum = #{memNum}

UPDATE voucher_log
   SET endDate = endDate + #{extendDays}
 WHERE memNum = #{memNum}

