mapper.insert:

    SELECT MEMBER_SEQ.NEXTVAL FROM DUAL


INSERT INTO MEMBER (
    memNum, memName, memGender, memBirthday, memPhone,
    memEmail, memAddr, memCreated, memUpdated, memProfile, memNote
) VALUES (
    #{memNum}, #{memName}, #{memGender}, #{memBirthday}, #{memPhone},
    #{memEmail}, #{memAddr}, SYSDATE, SYSDATE, #{memProfile}, #{memNote}
)

mapper.select:
SELECT memName
FROM MEMBER
WHERE memNum = #{memNum}

SELECT COUNT(*)
FROM MEMBER
WHERE memNum = #{memNum}
