mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rn, s.serviceId, s.codeBId, s.name, s.price, s.isActive, s.serviceValue, s.createdAt, s.updatedAt FROM service s WHERE s.isActive = 1
AND s.name LIKE '%' || #{keyword} || '%'
ORDER BY CASE WHEN s.codeBId LIKE '%PT%' THEN 0 ELSE 1 END, s.name DESC ) WHERE rn BETWEEN #{startRowNum} AND #{endRowNum}
