mapper.select:
SELECT * FROM ( SELECT result1.*, ROWNUM AS rnum FROM ( SELECT pr.productId, pr.name, pr.price, pr.isActive, pr.codeBId, cb.name AS codeBName, (NVL(pi.total_in, 0) - NVL(sa.total_out, 0) - NVL(si.total_sales, 0)) AS quantity FROM product pr LEFT JOIN codeB cb ON pr.codeBId = cb.codeBId LEFT JOIN ( SELECT productId, SUM(quantity) AS total_in FROM PURCHASE GROUP BY productId ) pi ON pr.productId = pi.productId LEFT JOIN ( SELECT productId, SUM(quantity) AS total_out FROM STOCKADJUSTMENT GROUP BY productId ) sa ON pr.productId = sa.productId LEFT JOIN ( SELECT productId, SUM(quantity) AS total_sales FROM SALES_ITEM WHERE status='ACTIVE' GROUP BY productId ) si ON pr.productId = si.productId
ORDER BY
cb.name pr.name pr.price quantity pr.productId
DESC ASC
) result1 ) WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}

SELECT * FROM ( SELECT result1.*, ROWNUM AS rnum FROM ( SELECT pr.productId, pr.name, pr.price, pr.isActive, pr.codeBId, cb.name AS codeBName FROM product pr LEFT JOIN codeB cb ON pr.codeBId = cb.codeBId
ORDER BY
cb.name pr.name pr.price pr.productId
DESC ASC
) result1 ) WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}

mapper.update:
UPDATE product
SET codeBId=#{codeBId}, name=#{name}, price=#{price}, isActive=#{isActive},
	updatedAt=#{updatedAt}, note=#{note}

	, profileImage=#{profileImage}

WHERE productId=#{productId}

		UPDATE product
		SET 
        isActive = #{isActive}  
        WHERE productId = #{productId}
