mapper.insert:

    SELECT SALESITEM_SEQ.NEXTVAL FROM DUAL


INSERT INTO SALESITEM (
    itemSalesId, productId, productName,
    empNum, quantity, unitPrice, totalAmount,  productType, status, createdAt, updatedAt
) VALUES (
    #{itemSalesId}, #{productId}, #{productName},
    #{empNum}, #{quantity}, #{unitPrice}, #{totalAmount},  #{productType}, #{status}, #{createdAt}, #{updatedAt}
)

mapper.update:
UPDATE SALESITEM
SET status = 'DELETED'
WHERE itemSalesId = #{itemSalesId}

mapper.select:
SELECT si.productId, si.productName, si.productType, SUM(si.quantity) AS totalQuantity, SUM(si.totalAmount) AS totalSalesAmount FROM sales_item si WHERE si.status = 'ACTIVE' AND si.createdAt BETWEEN TO_DATE(#{startDate}, 'YYYY-MM-DD') AND TO_DATE(#{endDate}, 'YYYY-MM-DD') + 0.99999
AND si.productId IN
#{itemId}
AND si.empNum = #{empNum}
GROUP BY si.productId, si.productName, si.productType ORDER BY totalSalesAmount DESC
