sql.where:

    AND si.createdAt >= TO_DATE(#{startDate}, 'YYYY-MM-DD')


    AND si.createdAt <= TO_DATE(#{endDate} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')



    AND p.codeBId IN

        #{cid}



    AND cb.name = #{categoryName}



    AND si.empNum = #{empNum}


    AND e.empEmail LIKE '%' || #{empEmail} || '%'



    AND p.name LIKE '%' || #{productNameKeyword} || '%'


AND si.status = 'ACTIVE'

mapper.update:
UPDATE sales_item
SET status = 'DELETED',    updatedAt = SYSDATE
WHERE itemSalesId = #{value}
