sql.where:


    AND ss.createdAt >= TO_DATE(#{startDate}, 'YYYY-MM-DD')


    AND ss.createdAt <= TO_DATE(#{endDate} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')




    AND UPPER(ss.serviceName) LIKE '%' || UPPER(#{serviceNameKeyword}) || '%'




    AND ss.memNum = #{memNum}




    AND ss.empNum = #{empNum}


AND ss.status = 'ACTIVE'
