mapper.select:
SELECT shNum, empNum, startTime, endTime, memo, color
FROM SCHEDULE
WHERE shNum = #{shNum}

mapper.insert:

    SELECT SCHEDULE_SEQ.NEXTVAL FROM DUAL

INSERT INTO SCHEDULE (
    shNum, empNum, startTime, endTime, memo, color
)
VALUES (
    #{shNum}, #{empNum}, #{startTime}, #{endTime}, #{memo}, #{color}
)
