mapper.select:
SELECT COUNT(*)
FROM SCHEDULE
WHERE empNum = #{empNum}
  AND codeBid = 'VACATION'
  AND startTime <= #{endTime}
  AND endTime   >= #{startTime}

SELECT COUNT(*)
FROM SCHEDULE
WHERE empNum = #{empNum}
  AND codeBid = 'VACATION'
  AND shNum  <> #{shNum}
  AND startTime <= #{endTime}
  AND endTime   >= #{startTime}
