mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT sv.serviceId, sv.name, sv.price, sv.isActive, sv.codeBId, sv.serviceValue, cb.name AS codeBName FROM service sv LEFT JOIN codeB cb ON sv.codeBId = cb.codeBId
ORDER BY
cb.name sv.name sv.price sv.serviceId
DESC ASC
) result1) WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}

mapper.update:
UPDATE service
SET codeBId=#{codeBId}, name=#{name}, price=#{price}, isActive=#{isActive},
	updatedAt=#{updatedAt}, note=#{note}



WHERE serviceId=#{serviceId}

		UPDATE service
		SET 
        isActive = #{isActive}  
        WHERE serviceId = #{serviceId}
