mapper.select:
SELECT * FROM ( SELECT sa.adjustmentId AS adjustmentId, sa.productId AS productId, sa.codeBId AS codeBId, sa.createdAt AS createdAt, sa.quantity AS quantity, sa.notes AS notes FROM STOCKADJUSTMENT sa WHERE sa.productId = #{productId}
UNION ALL
SELECT si.itemSalesId AS adjustmentId, si.productId AS productId, 'SALES' AS codeBId, si.createdAt AS createdAt, si.quantity AS quantity, '상품 판매' AS notes FROM SALESITEM si WHERE si.productId = #{productId} ) t ORDER BY t.createdAt DESC
