mapper.select:
SELECT COUNT(*)
FROM PURCHASE p
WHERE p.productId = #{productId}

SELECT adjustmentId, productId, codeBId, createdAt, quantity, notes FROM ( SELECT t.adjustmentId AS adjustmentId, t.productId AS productId, t.codeBId AS codeBId, t.createdAt AS createdAt, t.quantity AS quantity, t.notes AS notes, ROW_NUMBER() OVER (ORDER BY t.createdAt DESC, t.adjustmentId DESC) AS rn FROM ( SELECT sa.adjustmentId AS adjustmentId, sa.productId AS productId, sa.codeBId AS codeBId, sa.createdAt AS createdAt, sa.quantity AS quantity, sa.notes AS notes FROM STOCKADJUSTMENT sa WHERE sa.productId = #{productId}
UNION ALL
SELECT si.itemSalesId AS adjustmentId, si.productId AS productId, 'SALES' AS codeBId, si.createdAt AS createdAt, si.quantity AS quantity, '상품 판매' AS notes FROM SALES_ITEM si WHERE si.productId = #{productId} ) t ) WHERE rn > #{offset} AND rn <= #{offset} + #{size} ORDER BY createdAt DESC, adjustmentId DESC

SELECT COUNT(*)
FROM (
    SELECT adjustmentId FROM STOCKADJUSTMENT sa WHERE sa.productId = #{productId}
    UNION ALL
    SELECT itemSalesId FROM SALES_ITEM si WHERE si.productId = #{productId}
)

SELECT base.productId, base.productName, base.totalInbound, base.totalOutbound, base.totalSales, base.currentStock FROM ( SELECT p.productId AS productId, p.name AS productName, NVL(pi.total_in, 0) AS totalInbound, NVL(sa.total_out, 0) AS totalOutbound, NVL(si.total_sales, 0) AS totalSales, NVL(pi.total_in,0) - NVL(sa.total_out,0) - NVL(si.total_sales,0) AS currentStock, ROW_NUMBER() OVER (ORDER BY p.name ASC) AS rn FROM PRODUCT p LEFT JOIN ( SELECT productId, SUM(quantity) AS total_in FROM PURCHASE GROUP BY productId ) pi ON pi.productId = p.productId LEFT JOIN ( SELECT productId, SUM(quantity) AS total_out FROM STOCKADJUSTMENT GROUP BY productId ) sa ON sa.productId = p.productId LEFT JOIN ( SELECT productId, SUM(quantity) AS total_sales FROM SALES_ITEM GROUP BY productId ) si ON si.productId = p.productId
(LOWER(p.name) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(p.codeBId) LIKE '%' || LOWER(#{keyword}) || '%')
) base WHERE base.rn > #{offset} AND base.rn <= #{offset} + #{size} ORDER BY base.productName ASC
